$(function() {
    var exit = true;
    $('#tour').click(function() {
        altair_tour.init();
    });
});

altair_tour = {
    init: function() {

        // This tour guide is based on EnjoyHint plugin
        // for more info/documentation please check https://github.com/xbsoftware/enjoyhint

        // initialize instance
        var enjoyhint_instance = new EnjoyHint({});

        // config
        var enjoyhint_script_steps = [
            {
                "next #back": 'Hello, In this short tour guide I\'ll show you<br>' +
                'some features/components included in Altair Admin.<br>' +
                'This is the Quit button.<br>' +
                'Click "Next" to proceed.',
                shape : 'circle',
                radius: 30,
            },
            {
                "click .sg-editable-mark" : "Click on this Text to show mark form.",
                shape : 'circle',
                radius: 30,
                showSkip: false
            },
            {
                "click .editable-input input" : "Click on this field and add your mark.",
                showSkip: false
            },
            {
                "click .editable-submit" : "Click on this icon or enter to save the change.",
                shape : 'circle',
                radius: 30,
                "skipButton" : {text: "Finish"}
            },
        ];

        // set script config
        enjoyhint_instance.set(enjoyhint_script_steps);
        // run Enjoyhint script
        enjoyhint_instance.run();

    }
};
